export function createAriaHider(e,r="body > :not(script)"){const t=Array.from(document.querySelectorAll(r)).map((r=>{if(r.contains(e))return;const t=r.getAttribute("aria-hidden");return null!==t&&"false"!==t||r.setAttribute("aria-hidden","true"),{node:r,ariaHidden:t}}));return()=>{t.forEach((e=>{e&&(null===e.ariaHidden?e.node.removeAttribute("aria-hidden"):e.node.setAttribute("aria-hidden",e.ariaHidden))}))}}