export const hexToRGBA=($,t=1)=>{let r=0,e=0,x=0;return 4===$.length?(r=`0x${$[1]}${$[1]}`,e=`0x${$[2]}${$[2]}`,x=`0x${$[3]}${$[3]}`):7===$.length&&(r=`0x${$[1]}${$[2]}`,e=`0x${$[3]}${$[4]}`,x=`0x${$[5]}${$[6]}`),`rgba(${+r},${+e},${+x},${t})`};export const rgbToRgba=($,t=1)=>{const[r,e,x]=$.split(",").map(($=>Number.parseFloat($)));return`rgba(${r}, ${e}, ${x}, ${t>1?1:t<0?0:t})`};